document.querySelector(".Nav__search__input").addEventListener("input",e=>{e=e.target.value;let r=new RegExp(String.raw`${e}`,"gi");document.querySelectorAll(".Card").forEach(e=>{var t=e.querySelector(".Card__title").textContent;e.style.display&&e.style.removeProperty("display"),r.test(t)||(e.style.display="none")})});